export default {
  name: 'app',
  data: function data() {
    return {
      search_input: ""
    };
  },
  methods: {
    screenshot: function screenshot() {
      chrome.tabs.captureVisibleTab(null, {
        format: "png",
        quality: 100
      }, function (img_url) {
        chrome.downloads.download({
          url: img_url,
          saveAs: true
        }, function (res) {
          console.log(res);
        });
      });
    },
    getTabs: function getTabs() {
      chrome.tabs.query({}, function (tabs) {
        var urls = tabs.map(function (tab) {
          return tab.url;
        });
        chrome.storage.local.set({
          "test": urls
        });
      });
    },
    reloadTabs: function reloadTabs() {
      chrome.storage.local.get("test", function (urls) {
        console.log(urls);
      });
    }
  }
};
